<?php
// You are given two strings s and t.
// String t is generated by random shuffling string s and then add one more letter at a random position.
// Return the letter that was added to t.

// Example 1:
// Input: s = "abcd", t = "abcde"
// Output: "e"
// Explanation: 'e' is the letter that was added.

// Example 2:
// Input: s = "", t = "y"
// Output: "y"
function findTheDifference($s, $t)
{
  $string = "";
  $arrS = [];
  $arrT = [];
  for ($i = 0; $i < strlen($s); $i++) {
    if (isset($arrS[$s[$i]])) {
      $arrS[$s[$i]] += 1;
    } else {
      $arrS[$s[$i]] = 1;
    }
  }

  for ($i = 0; $i < strlen($t); $i++) {
    if (isset($arrT[$t[$i]])) {
      $arrT[$t[$i]] += 1;
    } else {
      $arrT[$t[$i]] = 1;
    }
  }

  if (count($arrS) < count($arrT)) {
    foreach ($arrT as $key =>  $val) {
      if (!isset($arrS[$key])) {
        $string = $key;
      }
    }
    return $string;
  } else if (count($arrS) > count($arrT)) {
    foreach ($arrS as $key =>  $val) {
      if (!isset($arrT[$key])) {
        $string = $key;
      }
    }
    return $string;
  } else if (count($arrS) == count($arrT)) {
    foreach ($arrS as $key =>  $val) {
      if (isset($arrT[$key])) {
        if ($arrT[$key] != $val) {
          $string = $key;
        }
      }
    }
    return $string;
  }
}

// var_dump(findTheDifference("abcd", "abcde"));
var_dump(findTheDifference("", ""));
